  import MsglistComponent from 'components/MsglistComponent.vue' ;
  import { useMailPageComposable } from 'src/composables/MailPageComposable' ;

  const {
    msgListOrBody,
    activeEmail,
    convertNameToInitial,
    
  } = useMailPageComposable() ;

</script>

<template>
<!-- style="width:90%;max-width:350px; -->
  <q-page class="q-py-xl q-mx-sm" >
    <div>
      <!-- 
        // ********************************************************************
        // This div is a toggled type of div that uses msgListOrBody variable *
        // to decide whether it should be shown or its opposite div should be *
        // shown instead.                                                     *
        // This div holds the inbox preview list where you can see sender and *
        // mail subject.                                                      *
        // ********************************************************************
      -->
      <div v-if="msgListOrBody===true">
        <q-markup-table class="no-border no-box-shadow no-outline">
          <tbody>
            <msglist-component v-for="emailsList in emailsLists" :key="emailsList.id"
              :senderName="emailsList.senderName"
              :date="emailsList.date"
              :subject="emailsList.subject"
              @click="{
                // ********************************************************************
                // First load the object that is carrying the email body's properties *
                // ********************************************************************
                activeEmail.senderName = emailsList.senderName ;
                activeEmail.senderEmail = emailsList.senderEmail ;
                activeEmail.date = emailsList.date ;
                activeEmail.initials = convertNameToInitial(emailsList.senderName) ;
                activeEmail.subject = emailsList.subject ;
                activeEmail.body = emailsList.body ;

                // *********************************
                // Now Switch Email Body Into View *
                // *********************************
                msgListOrBody = false ;
              }"
            ></msglist-component>
          </tbody>
        </q-markup-table>
      </div>

      <div v-if="msgListOrBody===false">
        <!-- 
          // ********************************************************
          // q-icon for arrow back button at the top of email body. *
          // onClick it will set msgListOrBody to true meaning that *
          // the div showing emails summary will be brought to view *
          // ********************************************************
        -->
        <q-icon 
          name="arrow_back" 
          class="q-mx-sm q-mb-md cursor-pointer" 
          color="primary" 
          size="20px" 
          @click="msgListOrBody=true"        
        /><br />

        <!-- 
          // ********************************************
          // The subject of the email is rendered here. *
          // ********************************************
        -->
        <div class="text-h5 q-mx-md q-mb-sm text-blue-grey-8">
          {{activeEmail.subject}}
        </div>

        <!-- 
          // **********************************************************************************
          // flex box for rounded initials and other propertiese at top of email body BEGIN.  *
          // **********************************************************************************
        -->
        <div class="row q-mx-sm">
          <div class="col-auto">
            <!-- 
              // ******************************************************************
              // The First and Last Name initials of the sender is rendered here  *
              // ******************************************************************
            -->
            <q-btn 
              round 
              color="blue-1"
              text-color="primary"
              :label="activeEmail.initials"
              size="20px"
              class="q-my-sm q-mx-sm"
            />
          </div>
          <!-- 

          -->
          <div class="col-5 self-center">
            <div class="text-caption text-weight-bold">{{activeEmail.senderName}}</div>
            <div class="text-caption">&lt;{{activeEmail.senderEmail}}&gt;</div>
          </div>
          <!-- 

          -->
          <div class="col self-center">
            <q-icon name="drafts" class="q-mx-sm" />{{activeEmail.date}}
          </div>
        </div>
        <!-- 
          // ************************************************************
          // flex box for rounded initials at top of email body ENDS.  *
          // ************************************************************
        -->

        <!-- 
          // ************************************************************
          // email body BEGIN.                                          *
          // ************************************************************
        -->
        <div class="text-body2 q-mx-md q-my-md" v-html="activeEmail.body">
        </div>
        <!-- 
          // ************************************************************
          // email body ENDS.                                           *
          // ************************************************************
        -->
      </div>
    </div>
  </q-page>
</template>
